mapper.select:
select count(*)
from tb_jjiminfo
where pfmno=#{performanceNo}
  and mmbno=#{memberNo}

select 
    count(*) 
from tb_jjiminfo
where pfmno=#{performanceNo}
  and mmbno=#{memberNo}

mapper.insert:
update tb_jjiminfo set
    mmbno=#{memberNo}
where pfmno=#{performanceNo}

mapper.delete:
delete from tb_jjiminfo
where pfmno=#{performanceNo}
  and mmbno=#{memberNo}
